(5723, 1, '2018-06-05 14:40:32', '2018-06-05 09:10:32', '<article class="article-section bg-white payment-article">\r\n  <div class="text-wrap">\r\n    <h3 class="article-title">No Worries, Easy Payment</h3>\r\n    <p class="article-info  text-center">The Easiest Way Of Payment By Paypal </p>\r\n\r\n  </div>\r\n  <div class="img-wrap text-center">\r\n<img src="/wp-content/uploads/2018/05/payment.png"/ class="payment-image">\r\n      </div>\r\n</article>\r\n<article class="payment-wrapper">\r\n        <header class="payment-header">\r\n            <div id="home-about-section" class="section-title"><h2 class="widget-title">Choose a payment method</h2>\r\n<div>read our <a href="/terms-and-conditions/">terms and conditions</a></div>\r\n<div class="seperator"> <span> <i class="fa fa-smile-o" aria-hidden="true"></i> </span></div></div>\r\n            <div class="pamt-btn-wrap">\r\n<label class="payment-btn"><input checked onchange="togglepaymentform(''paytm'')" type="radio" name="payment-method" /> <span>Paytm</span></label>\r\n                <label class="payment-btn"><input onchange="togglepaymentform(''paypal'')" type="radio" name="payment-method" /> <span>Paypal</span></label>\r\n                \r\n            </div>\r\n        </header>\r\n        <section class="payment-form hidden" id="paypal-form">\r\n<h4 class="text-center">Choose currency</h4>\r\n<div class="text-center" id="curr-wrap">\r\n<label><input onchange="toggle_currency(''inr'')" type="radio" name="currency-type" value="inr"/> INR</label><label><input onchange="toggle_currency(''usd'')" type="radio" name="currency-type" value="usd"/> USD</label>\r\n</div>\r\n<section id="paypal-inr" class="hidden">\r\n            <form class="text-center" action="https://www.paypal.com/cgi-bin/webscr" method="post" target="_top">\r\n                <input type="hidden" name="cmd" value="_s-xclick">\r\n                <input type="hidden" name="hosted_button_id" value="48AY5W3N5NUZG">\r\n                <input type="image" src="https://www.paypalobjects.com/en_GB/i/btn/btn_paynowCC_LG.gif" border="0" name="submit" alt="PayPal â€“ The safer, easier way to pay online!">\r\n                <img alt="" border="0" src="https://www.paypalobjects.com/en_GB/i/scr/pixel.gif" width="1" height="1">\r\n            </form>\r\n</section>\r\n<section id="paypal-usd" class="text-center hidden">\r\n<form class="text-center" action="https://www.paypal.com/cgi-bin/webscr" method="post" target="_top">\r\n<input type="hidden" name="cmd" value="_s-xclick">\r\n<input type="hidden" name="encrypted" value="-----BEGIN PKCS7----------END PKCS7-----\r\n">\r\n<input type="image" src="https://www.paypalobjects.com/en_US/i/btn/btn_paynowCC_LG.gif" border="0" name="submit" alt="PayPal - The safer, easier way to pay online!">\r\n<img alt="" border="0" src="https://www.paypalobjects.com/en_GB/i/scr/pixel.gif" width="1" height="1">\r\n</form>\r\n\r\n\r\n</section>\r\n        </section>\r\n        <section class="payment-form " id="paytm-form">\r\n<img src="/wp-content/uploads/2018/06/paytm.png" alt="pay through Paytm"/>\r\n            [paytmcheckout]\r\n        </section>\r\n    </article>\r\n<script>\r\nfunction togglepaymentform(target) {\r\n    if (target == ''paypal'') {\r\n        document.getElementById("paypal-form").classList.remove(''hidden'');\r\n        document.getElementById("paytm-form").classList.add(''hidden'');\r\n    }\r\n    else {\r\n        document.getElementById("paytm-form").classList.remove(''hidden'');\r\n        document.getElementById("paypal-form").classList.add(''hidden'');\r\n    }\r\n}function toggle_currency(selected_curr) {\r\n    if (selected_curr == ''inr'') {\r\n        document.getElementById("paypal-inr").classList.remove(''hidden'');\r\n        document.getElementById("paypal-usd").classList.add(''hidden'');\r\n    }\r\n    else {\r\n        document.getElementById("paypal-usd").classList.remove(''hidden'');\r\n        document.getElementById("paypal-inr").classList.add(''hidden'');\r\n    }\r\n}\r\n</script>\r\n', 'Payments', '', 'inherit', 'closed', 'closed', '', '5572-revision-v1', '', '', '2018-06-05 14:40:32', '2018-06-05 09:10:32', '', 5572, 'http://www.mobprodigital.com/5572-revision-v1/', 0, 'revision', '', 0),
